import './App.css';
import About from './components/About';
import Navbar from './components/Navbar';
import StateEx from './components/StateEx';
import TextForm from './components/TextForm';
import { useState } from 'react';
import Alert from './components/Alert';
// import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";


function App() {
  const [mode, setMode] = useState('light'); // Whether dark mode is enabled or not

   const toogleMode = () =>{
    if(mode === 'light'){
      setMode('dark');
      document.body.style.backgroundColor = '#042743';
      showAlert("Dark mode has been enabled","success");
      document.title = "My first React App-Dark"
    }
    else{
      setMode('light');
      document.body.style.backgroundColor = 'white';
      showAlert("light mode has been enabled","success");
      document.title = "My first React App-light"


    }
   }

  const [alert, setAlert] = useState(null);
  const showAlert=(messgae, type)=>{
    setAlert({
      msg : messgae,
      type : type
    })
    setTimeout(() => {
      setAlert(null)
    }, 3000);
  }

  return (
    <>
    <router>
      <Navbar title = "MyNavbar" aboutText = "About Us" mode={mode} toggleMode = {toogleMode}/>
      <Alert alert = {alert}/>
      <StateEx/>
      <About />
      <TextForm heading = "Enter text to analayed Below" mode={mode}/>
      <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/">
            <TextForm heading = "Enter text to analayed Below" mode={mode}/>
          </Route>
      </Switch>
      </router>

    </>
  );
}

export default App;
